public class IntPreference {
  private final SharedPreferences preferences;
  private final String key;
  private final int integerDefaultValue;
  private final String defaultValue;
  //using String because of getInt issues mentioned here:
  // https://github.com/scottyab/secure-preferences/issues/37


  public IntPreference(final SharedPreferences preferences, final String key) {
    this(preferences, key, -1);
  }

  public IntPreference(
      final SharedPreferences preferences,
      final String key,
      final int defaultValue) {
    this.preferences = preferences;
    this.key = key;
    this.integerDefaultValue = defaultValue;
    this.defaultValue = String.valueOf(defaultValue);
  }

  public void delete() {
    preferences.edit().remove(key).apply();
  }

  public int get() {

    int finalData = integerDefaultValue;
    String prefData = preferences.getString(key, defaultValue);
    try {
      finalData = Integer.parseInt(prefData);
    } catch (ClassCastException | NumberFormatException | NullPointerException ex) {
      Timber.e(ex.getMessage());
    }
    return finalData;

  }

  public boolean isSet() {
    return preferences.contains(key);
  }

  public void set(final int value) {
    preferences.edit().putString(key, String.valueOf(value)).apply();
  }
}

